import { Component,useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";
import PassengerInput from './PassengerInput';
import ListPassenger from './ListPassenger';
import Header from './Header';
import {gql, useMutation, useLazyQuery} from "@apollo/client";
import Loader from "react-loader-spinner";
import GetPassengerBy from "./GetPassengerBy";

const GetAllPassenger=gql`
query MyQuery {
    passenger {
      id
      nama
      jenisKelamin
      umur
    }
  }`

const DeleteById=gql`
mutation MyMutation($id: Int!) {
    delete_passenger_by_pk(id: $id) {
    nama
    umur
    }
}  
`
const AddData=gql`
mutation MyMutation2($object: passenger_insert_input = {}) {
    insert_passenger_one(object: $object){
      id
    }
  }
`

const GetDataById=gql`
query MyQuery2($id: Int!) {
    passenger(where: {id: {_eq: $id}}) {
      id
      nama
      umur
    }
  }

`

function Home(){
    const [getData,{data, loading, error}]=useLazyQuery(GetAllPassenger);
    const [searchDataById,{data:singleData, loading:load, error:err}]=useLazyQuery(GetDataById);
    const [deletePassenger] = useMutation(DeleteById, { refetchQueries: [GetAllPassenger] })
    const [AddNewData] = useMutation(AddData, { refetchQueries: [GetAllPassenger] })
    const [dataPassengerById, setDataPassengerById] =useState()
    const [id, setId] = useState(null)

    useEffect(() => {
        getData()
    },[data])

    useEffect(() => {
        if (!load) {
            setDataPassengerById(singleData?.passenger)
        }
    }, [load, singleData])
    
    if (loading){
        return <Loader type="ThreeDots" height={100} width={100} />
    }
    if (error){
        console.log("error : ",error)
        return null
    }
    
        
    if (load){
        console.log("loading get single data")
        return <Loader type="ThreeDots" height={100} width={100} />

    }
    if (err){
        console.log("error : ",error)
        return null
    }
    const hapusPengunjung = (id) => {
        deletePassenger({
            variables:{
                id
            }
        })
    }

    const tambahPengunjung = newUser => {

        AddNewData({
            variables:{
                object:newUser
                
            }
        })
    };

    const cariPengunjung=(id)=>{
        console.log("id :", id)
        searchDataById({
            variables:{
                id
            }
        })
        console.log("single data", singleData)
        console.log("error single data", err)

        return singleData
    }

    return (
        <div>
            <Header/>
            {/* <h1>{ListPassenger}</h1> */}
            <p>"ss",{JSON.stringify(singleData?.passenger)}</p>
            <GetPassengerBy cariPengunjung={cariPengunjung} data={dataPassengerById}/>
            {/* <label for="ID">Input id passenger:</label>
            <input type="text" id="id" name="id" onChange={onChange}/><br/>
            <button type="submit" onClick={submitHandle} >cari</button>
            <div>
                <p>{JSON.stringify(dataOnePassenger?.passenger)}</p>
            </div> */}
            {loading ? <Loader type="ThreeDots" height={100} width={100} /> : <ListPassenger data={data?.passenger} hapusPengunjung={hapusPengunjung}/>
            }
            <PassengerInput
                tambahPengunjung={tambahPengunjung}
            />
        </div>
        )
}

